        if (error) {
          throw error;
        }
        padding = 50;
        height = graph.data("height");
        width = graph.parent().width();
        xlimits = data["limits"][0];
        x = d3.scaleTime().domain([new Date(xlimits[0] * 1000), new Date(xlimits[1] * 1000)]).range([padding, width - padding]);
