jQuery(function() {
    var $content = $('#personal-data-content');
    var $submenu = $('#personal-data-submenu');

    if ($submenu.length) {
        var hideContent = function() {
            $content.find('.js-personal-content').hide();
        };

        var removeActive = function() {
            $submenu.find('li.active').removeClass('active');
        };

        var showTab = function(submenu) {
            hideContent();
            removeActive();
            var tab = $("a[href="+submenu+"]", $submenu);
            tab.parent().addClass('active');
            $(submenu).show();
        };

        $submenu.on('click', 'a.js-change-tab', function(evt) {
            evt.preventDefault();
            var target = $(this).attr('href');
            window.location.hash = target;
            showTab(target);
        });

        $content.on('click', 'a.js-change-tab', function(evt) {
            evt.preventDefault();
            var target = $(this).attr('href');
            window.location.hash = target;
            showTab(target);
        });

        if (window.location.hash) {
            showTab(window.location.hash);
            $("#errors_in_form").prependTo(window.location.hash);
            $(".js-personal-content:not("+window.location.hash+") .alert-nexttolabel", $content).remove();
        }
    }
});
jQuery(function($) {
    var $entity  = $('#collaboration_ccc_entity');
    var $office  = $('#collaboration_ccc_office');
    var $dc      = $('#collaboration_ccc_dc');
    var $account = $('#collaboration_ccc_account');

    var onlyNumbersWithMaxLength = function(maxLength, $nextField) {
        return function(evt) {
            var val = $(this).val();
            val = val.replace(/[^0-9]/g, ""); // Remove anything but numbers
            $(this).val(val);

            if (val.length >= maxLength) {
                if ($nextField) {
                    $nextField.focus();
                } else {
                    $(this).blur();
                }
            }
        };
    };

    $entity.on('keyup', onlyNumbersWithMaxLength(4, $office));
    $office.on('keyup', onlyNumbersWithMaxLength(4, $dc));
    $dc.on('keyup', onlyNumbersWithMaxLength(2, $account));
    $account.on('keyup', onlyNumbersWithMaxLength(10));
});
