  /**
   * <p>Encapsulates a point of interest in an image containing a barcode. Typically, this
   * would be the location of a finder pattern or the corner of the barcode, for example.</p>
   *
   * @author Sean Owen
   */
  class ResultPoint {
      constructor(x, y) {
          this.x = x;
          this.y = y;
      }
      getX() {
          return this.x;
      }
      getY() {
          return this.y;
      }
      /*@Override*/
      equals(other) {
          if (other instanceof ResultPoint) {
              const otherPoint = other;
              return this.x === otherPoint.x && this.y === otherPoint.y;
          }
          return false;
      }
      /*@Override*/
      hashCode() {
          return 31 * Float.floatToIntBits(this.x) + Float.floatToIntBits(this.y);
      }
      /*@Override*/
      toString() {
          return '(' + this.x + ',' + this.y + ')';
      }
      /**
       * Orders an array of three ResultPoints in an order [A,B,C] such that AB is less than AC
       * and BC is less than AC, and the angle between BC and BA is less than 180 degrees.
       *
       * @param patterns array of three {@code ResultPoint} to order
       */
      static orderBestPatterns(patterns) {
          // Find distances between pattern centers
          const zeroOneDistance = this.distance(patterns[0], patterns[1]);
          const oneTwoDistance = this.distance(patterns[1], patterns[2]);
          const zeroTwoDistance = this.distance(patterns[0], patterns[2]);
          let pointA;
          let pointB;
          let pointC;
          // Assume one closest to other two is B; A and C will just be guesses at first
          if (oneTwoDistance >= zeroOneDistance && oneTwoDistance >= zeroTwoDistance) {
              pointB = patterns[0];
              pointA = patterns[1];
              pointC = patterns[2];
          }
          else if (zeroTwoDistance >= oneTwoDistance && zeroTwoDistance >= zeroOneDistance) {
              pointB = patterns[1];
              pointA = patterns[0];
              pointC = patterns[2];
          }
          else {
              pointB = patterns[2];
              pointA = patterns[0];
              pointC = patterns[1];
          }
          // Use cross product to figure out whether A and C are correct or flipped.
          // This asks whether BC x BA has a positive z component, which is the arrangement
          // we want for A, B, C. If it's negative, then we've got it flipped around and
          // should swap A and C.
          if (this.crossProductZ(pointA, pointB, pointC) < 0.0) {
              const temp = pointA;
              pointA = pointC;
              pointC = temp;
          }
          patterns[0] = pointA;
          patterns[1] = pointB;
          patterns[2] = pointC;
      }
      /**
       * @param pattern1 first pattern
       * @param pattern2 second pattern
       * @return distance between two points
       */
      static distance(pattern1, pattern2) {
          return MathUtils.distance(pattern1.x, pattern1.y, pattern2.x, pattern2.y);
      }
      /**
       * Returns the z component of the cross product between vectors BC and BA.
       */
      static crossProductZ(pointA, pointB, pointC) {
          const bX = pointB.x;
          const bY = pointB.y;
          return ((pointC.x - bX) * (pointA.y - bY)) - ((pointC.y - bY) * (pointA.x - bX));
      }
  }

